import React, {useState} from 'react'
import "./styles/CribsFilter.css"

function CribsFilter({handleAdd}) {
  const [location, setLocation] = useState("")
  const [area, setArea] = useState("")
  const [bed, setBed] = useState("")
  const [bath, setBath] = useState("")
  const [price, setPrice] = useState("")
  const [images, setImages] = useState("")

  function handleSubmit(e){
    e.preventDefault();
    const imagesArray = images.split(", ")

    fetch("http://localhost:3000/cribs", {
    method: 'POST',
    headers: {"Content-Type": "application/json"},
    body: JSON.stringify({
      location: location,
      area: area,
      bed: bed,
      bath: bath,
      price: price,
      images: images
    })

    }).then(response => response.json())
    .then(newCrib=>handleAdd(newCrib))
  }

  return (
    <>
    <div className="name">
      LIST YOUR CRIB
    </div>

     <form onSubmit={handleSubmit} >
         <input value = {location} onChange= {(e)=>setLocation(e.target.value)} placeholder="Location" />
        <br/>
        <input value ={area} onChange= {(e)=>setArea(e.target.value)} placeholder="Area"/>
        <br/>
        <input value ={bed}  onChange= {(e)=>setBed(e.target.value)} placeholder="Beds"/>
        <br/>
       <input  value ={bath}  onChange= {(e)=>setBath(e.target.value)} placeholder="Bath"/>
        <br/>
        <input value ={price} onChange= {(e)=>setPrice(e.target.value)} placeholder="Price"/>
        <br/>
        <input  value ={images} onChange= {(e)=>setImages(e.target.value)} placeholder="Image-1"/>
        <input  value ={images} onChange= {(e)=>setImages(e.target.value)} placeholder="Image-2"/>
        <input  value ={images} onChange= {(e)=>setImages(e.target.value)} placeholder="Image-3"/>
        <input  value ={images} onChange= {(e)=>setImages(e.target.value)} placeholder="Image-4"/>
        </form>

    <div className="button">
        <button>SUBMIT</button>
        </div>

    </>
  )
}

export default CribsFilter
