import React, {useState} from 'react'

function CribsFilter({handleAdd}) {
  const [location, setLocation] = useState("")
  const [area, setArea] = useState("")
  const [bed, setBed] = useState("")
  const [bath, setBath] = useState("")
  const [price, setPrice] = useState("")
  const [images, setImages] = useState("")

  function handleSubmit(e){
    e.preventDefault();
    fetch("http://localhost:3000/cribs", {
    method: 'POST',
    headers: {"Content-Type": "application/json"},
    body: JSON.stringify({
      location: location,
      area: area,
      bed: bed,
      bath: bath,
      price: price,
      images: images
    })

    }).then(response => response.json())
    .then(newCrib=>handleAdd(newCrib))
  }

  return (
    <>
    <div >
      List your crib
    </div>
     <form onSubmit={handleSubmit} >
        location: <input value = {location} onChange= {(e)=>setLocation(e.target.value)}type="text" name="fname"/>
        <br/>
        area: <input value ={area} onChange= {(e)=>setArea(e.target.value)} type="text" name="fname"/>
        <br/>
        bed: <input value ={bed}  onChange= {(e)=>setBed(e.target.value)} type="text" name="fname"/>
        <br/>
        bath: <input  value ={bath}  onChange= {(e)=>setBath(e.target.value)} type="text" name="fname"/>
        <br/>
        Price: <input value ={price} onChange= {(e)=>setPrice(e.target.value)} type="text" name="fname"/>
        <br/>
        images: <input  value ={images} onChange= {(e)=>setImages(e.target.value)} type="text" name="fname"/>


        <button>SUBMIT</button>
    </form>
    </>
  )
}

export default CribsFilter
