import React, {useState} from 'react'

function NewAgentForm({handleAdd}) {
    const [name, setName]= useState("")
    const [office, setOffice]= useState("")
    const [mobile, setMobile]= useState("")
    const [email, setEmail]= useState("")
    const [image, setImage]= useState("")
    const [position, setPosition]= useState("")
    const [about, setAbout]= useState("")

    function handleSubmit(e){
        e.preventDefault()
        fetch("http://localhost:3000/employees", {
        method: "POST",
        headers:{
            "content-type": "application/json"
        },
        body: JSON.stringify({
            name:name,
            office:office,
            mobile: mobile,
            email: email,
            image:image,
            position: position,
            about: about,
        })
        })
        .then(response => response.json())
        .then(newAgent=> handleAdd(newAgent))

    }
  return (
   <>
    <div className="form-box">
        <form onSubmit= {handleSubmit}>
            <input type="text"placeholder="Your name" 
                value={name}
                onChange= {(e)=>setName(e.target.value)}
            ></input>
            <input type="text"placeholder="Your office"
                 value={office}
                 onChange= {(e)=>setOffice(e.target.value)}
            ></input>
            <input type="number"placeholder="Your phone"
                 value={mobile}
                 onChange= {(e)=>setMobile(e.target.value)}
            ></input>
            <input type="email"placeholder="Your email"
                 value={email}
                 onChange= {(e)=>setEmail(e.target.value)}
            ></input>
            <input type="text"placeholder="Your Picture"
                 value={image}
                 onChange= {(e)=>setImage(e.target.value)}
            ></input>
            <input type="text"placeholder="Your Position"
                 value={position}
                 onChange= {(e)=>setPosition(e.target.value)}
            ></input>
            <input type="text"placeholder="About You "
                 value={about}
                 onChange= {(e)=>setAbout(e.target.value)}
            ></input>
            <button>SUBMIT</button>
        </form>
    </div>
   </>
  )
}

export default NewAgentForm
